import { Container } from "@mui/system";
import React, { useState } from "react";
import LoginButton from "../utilities/LoginButton";
import styles from "./signUp.module.css";
import CompanyName from "../assets/Instagram.png";

const SignUp = () => {
  const [mobileEmail, setMobileEmail] = useState("");
  const [name, setName] = useState("");
  const [userName, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [mobilevalidation, setMobileval] = useState(false);
  const [nameValidation, setNameval] = useState(false);
  const [userNameValidation, setuserNameval] = useState(false);
  const [passwordValidation, setpasswordval] = useState(false);

  const submitHandler = () => {
    let form = {
      mobileEmail: mobileEmail,
      name: name,
      userName: userName,
      password: password,
    };
    if (!form.mobileEmail.length === 10) {
      setMobileval(true);
      console.log("mobile no not valid");
      return;
    } else if (form.name.length < 4) {
      setNameval(true);
      console.log("name not valid");

      return;
    } else if (!form.userName.includes(Number)) {
      setuserNameval(true);
      console.log("username not valid");

      return;
    } else if (!form.password.includes(Number)) {
      setpasswordval(true);
      console.log("password not valid");
    } else {
      setMobileEmail("");
      setName("");
      setUsername("");
      setPassword("");
      console.log(form);
    }
  };

  return (
    <div>
      <Container
        maxWidth="sm"
        sx={{
          backgroundColor: "white",
          border: "groove",
          height: 550,
          width: 390,
          marginTop: 11,
        }}
      >
        <div className={styles.logocontainer}>
          <img
            className={styles.logo}
            src={CompanyName}
            alt="logo"
            height={80}
            width={200}
          />
        </div>
        <form onSubmit={submitHandler}>
          <input
            onChange={(e) => {
              setMobileEmail(e.target.value);
              console.log(mobileEmail);
            }}
            value={mobileEmail}
            className={styles.input}
            type="text"
            placeholder=" Mobile number or email address"
          />
          <br />
          <input
            onChange={(e) => {
              setName(e.target.value);
              console.log(name);
            }}
            value={name}
            className={styles.input}
            type="text"
            placeholder=" Full Name"
          />
          <br />
          <input
            onChange={(e) => {
              setUsername(e.target.value);
              console.log(userName);
            }}
            value={userName}
            className={styles.input}
            type="text"
            placeholder=" Username"
          />
          <br />
          <input
            onChange={(e) => {
              setPassword(e.target.value);
              console.log(password);
            }}
            value={password}
            className={styles.input}
            type="password"
            placeholder=" Password"
          />

          <p className={styles.terms}>
            By signing up, you agree to our Terms, Privacy Policy and Cookies
            Policy.
          </p>

          <LoginButton className={styles.loginbutton} type="submit">
            Sign Up
          </LoginButton>
        </form>
        <p className={styles.signup}>
          Have an account
          <a className={styles.link} href="login">
            {" "}
            Log in
          </a>
        </p>
      </Container>
    </div>
  );
};

export default SignUp;
